import React, {useRef,useEffect} from 'react';
import {Button} from "react-bootstrap";
import {Link} from "react-router-dom";

// icons
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faSearch } from '@fortawesome/free-solid-svg-icons'

// gsap animation
import {TweenMax,Power3} from "gsap";

// image
import Car from "../../images/car.png";

// styles
import "./banner.styles.scss";

const Banner = () =>{

let banner= useRef(null);
let carImg = useRef(null);

useEffect(() => {
    TweenMax.to(banner,.8,{opacity:1,y:80,eae:Power3});
    TweenMax.from(carImg,.8,{right:'30%',x:20,eae:Power3.easeIn});
},[]);

return <div className="container-fluid" ref={el => banner = el}>
    <div className="row">
        <div className="col-md-6 banner-image" ref={el => carImg = el}><img src={Car} alt="First slide" /></div>
        <div className="col-md-6 d-flex flex-column align-text-center banner-text-container">IS YOUR CAR DUE FOR SERVICE? <p className="mt-3">FIND A GARAGE IN YOUR NEIGHBOURHOOD, RIGHT NOW!</p>            
         <Button className="btn btn-dark mt-2 pl-5 pr-5 pt-3 pb-3">
            <Link to="/search">Search Now <FontAwesomeIcon icon={faSearch} className="ml-3" /></Link>
         </Button></div>
    </div>
</div>
}

export default Banner;
